import React, { useState } from 'react';
import AuthService from '../shared/AuthService';

function Login({ onLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null); // Add this line

  const handleSubmit = (event) => {
    event.preventDefault();
    AuthService.login({ user: { email, password } })
      .then(user => {
        onLogin(user);
        setError(null); // Clear the error message upon successful login
      })
      .catch(error => {
        setError(error.message); // Set the error message upon failure
        console.error(error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Email:
        <input type="email" value={email} onChange={e => setEmail(e.target.value)} required />
      </label>
      <label>
        Password:
        <input type="password" value={password} onChange={e => setPassword(e.target.value)} required />
      </label>
      {error && <p>{"incorrect password or login"}</p>} {/* Display the error message if it exists */}
      <button type="submit">Login</button>
    </form>
  );
}

export default Login;
