import React, { useState, useEffect } from 'react';
import AuthService from './shared/AuthService'; // Adjusted path
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom'; // Added Navigate
import Login from './pages/Login';
import AdminPanel from './pages/AdminPanel';


function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const user = AuthService.getCurrentUser();
    setUser(user);
  }, []);

  const handleLogin = (credentials) => {
    AuthService.login(credentials)
      .then(user => {
        setUser(user);
      });
  };

  const handleLogout = () => {
    AuthService.logout();
    setUser(null);
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginWrapper user={user} onLogin={handleLogin} />} />
        <Route path="/admin" element={<AdminPanelWrapper user={user} onLogout={handleLogout} />} />
        <Route path="/" element={<Navigate to="/login" />} /> {/* Redirects to /login */}
      </Routes>
    </Router>
  );
}

function LoginWrapper({ user, onLogin }) {
  const navigate = useNavigate();
  useEffect(() => {
    if (user) {
      navigate('/admin');
    }
  }, [user, navigate]);

  return <Login onLogin={onLogin} />;
}

function AdminPanelWrapper({ user, onLogout }) {
  const navigate = useNavigate();
  useEffect(() => {
    if (!user) {
      navigate('/login');
    }
  }, [user, navigate]);

  return <AdminPanel onLogout={onLogout} />;
}

export default App;
