import React from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import AuthService from '../AuthService';
import { useNavigate } from 'react-router-dom'; // import useNavigate

function Header({ user, onLogout }) {
  const navigate = useNavigate(); // get navigate function


  const handleProfile = () => {
    navigate('/profile');
  };

  const handleLogout = () => {
    AuthService.logout();
    onLogout();
  };

  const handleLogin = () => {
    navigate('/login'); // navigate to login page
  };

  const handleSignup = () => {
    navigate('/sign-up'); // navigate to signup page
  };
  const handleAdmin = () => {
    navigate('/admin'); // navigate to signup page
  };

  return (
    <AppBar position="fixed" sx={{ backgroundColor: '#000' }}>
      <Toolbar>
        <Box sx={{ flexGrow: 1 }} />
        <Typography variant="h6" component="div" sx={{ position: 'absolute', left: '50%', transform: 'translateX(-50%)' }}>
          FieldForge
        </Typography>
        <Box sx={{ flexGrow: 1, display: 'flex', justifyContent: 'flex-end' }}>
          {user ? (
            <>
              {user.is_staff === true && <Button color="inherit" onClick={handleAdmin}>Admin</Button>}
              <Button color="inherit" onClick={handleProfile}>Profile</Button>
              <Button color="inherit" onClick={handleLogout}>Logout</Button>
            </>
          ) : (
            <>
              <Button color="inherit" onClick={handleLogin}>Login</Button>
              <Button color="inherit" onClick={handleSignup}>Sign up</Button>
            </>
          )}
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default Header;
