import React, { useState, useEffect } from 'react';
import MinesService from '../api/MinesService';
import { useTranslation } from 'react-i18next';
import './Mines.css';
import {
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
    Typography,
    Button,
    TextField,
    Dialog,
    DialogActions,
    DialogContent,
    DialogContentText,
    DialogTitle
} from '@mui/material';


function Mines() {
    const [mines, setMines] = useState(null);
    const { t } = useTranslation();
    const [open, setOpen] = useState(false);
    const [newMine, setNewMine] = useState({ uuid: '', type: '', range: '', is_activated: false, is_defused: false });

    const fetchMines = async () => {
        try {
            const mines = await MinesService.getMines();
            setMines(mines);
        } catch (error) {
            console.error('Failed to fetch mines:', error);
        }
    };

    const handleDeleteMine = (uuid) => {
        setMines(mines.filter(mine => mine.uuid !== uuid));
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewMine(prevState => ({ ...prevState, [name]: value }));
    };

    // const handleBanUser = async (uuid) => {
    //     console.log(`Ban user with UUID: ${uuid}`);
    //     try {
    //         const result = await UsersService.banUser(uuid);
    //         console.log(result);
    //         // After successfully banning the user, you might want to remove them from the users state or refetch the users
    //         fetchUsers();
    //     } catch (error) {
    //         console.error('Failed to ban user:', error);
    //     }
    // };
    useEffect(() => {
        fetchMines();
    }, []);

    if (!mines) {
        return <Typography>{t("There is no mines in system")}</Typography>;
    }

    return (
        <div>
            <Typography variant="h4" className={classes.title}>
                Admin Panel Mines
            </Typography>
            <Button variant="contained" color="primary" className={classes.addButton} onClick={() => setOpen(true)}>
                Add New Mine
            </Button>
            <Dialog open={open} onClose={() => setOpen(false)}>
                <DialogTitle>Add New Mine</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        To add a new mine, please enter the mine type, range, activation status, and defused status here.
                    </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        name="type"
                        label="Type"
                        type="text"
                        fullWidth
                        value={newMine.type}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="dense"
                        name="range"
                        label="Range"
                        type="number"
                        fullWidth
                        value={newMine.range}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="dense"
                        name="is_activated"
                        label="Is Activated"
                        type="checkbox"
                        fullWidth
                        checked={newMine.is_activated}
                        onChange={(e) => setNewMine(prevState => ({ ...prevState, is_activated: e.target.checked }))}
                    />
                    <TextField
                        margin="dense"
                        name="is_defused"
                        label="Is Defused"
                        type="checkbox"
                        fullWidth
                        checked={newMine.is_defused}
                        onChange={(e) => setNewMine(prevState => ({ ...prevState, is_defused: e.target.checked }))}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setOpen(false)} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={handleAddMine} color="primary">
                        Add
                    </Button>
                </DialogActions>
            </Dialog>
            <TableContainer component={Paper}>
                <Table className={classes.table} aria-label="mines table">
                    <TableHead>
                        <TableRow>
                            <TableCell>UUID</TableCell>
                            <TableCell>Type</TableCell>
                            <TableCell>Range</TableCell>
                            <TableCell>Is Activated</TableCell>
                            <TableCell>Is Defused</TableCell>
                            <TableCell>Actions</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {mines.map((mine) => (
                            <TableRow key={mine.uuid}>
                                <TableCell>{mine.uuid}</TableCell>
                                <TableCell>{mine.type}</TableCell>
                                <TableCell>{mine.range}</TableCell>
                                <TableCell>{mine.is_activated ? 'Yes' : 'No'}</TableCell>
                                <TableCell>{mine.is_defused ? 'Yes' : 'No'}</TableCell>
                                <TableCell>
                                    <Button
                                        variant="contained"
                                        color="secondary"
                                        className={classes.button}
                                        onClick={() => handleDeleteMine(mine.uuid)}
                                    >
                                        Delete
                                    </Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    );
};


export default Mines;
