import React, { useState } from 'react';
import AuthService from '../shared/AuthService';

function Login({ onLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    AuthService.login({ user: { email, password } })
      .then(user => {
        onLogin(user);
        // Handle successful response here. For example, you can redirect the user to a different page.
      })
      .catch(error => {
        // Handle error response here. For example, you can display an error message to the user.
        console.error(error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Email:
        <input type="email" value={email} onChange={e => setEmail(e.target.value)} required />
      </label>
      <label>
        Password:
        <input type="password" value={password} onChange={e => setPassword(e.target.value)} required />
      </label>
      <button type="submit">Login</button>
    </form>
  );
}

export default Login;
