function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const user = AuthService.getCurrentUser();
    setUser(user);
  }, []);

  const handleLogin = (credentials) => {
    AuthService.login(credentials)
      .then(user => {
        setUser(user);
      });
  };

  const handleLogout = () => {
    AuthService.logout();
    setUser(null);
  };

  return (
    <Router>
      <Route path="/login" element={<LoginWrapper user={user} onLogin={handleLogin} />} />
      <Route path="/admin" element={<AdminPanelWrapper user={user} onLogout={handleLogout} />} />
    </Router>
  );
}

function LoginWrapper({ user, onLogin }) {
  const navigate = useNavigate();
  useEffect(() => {
    if (user) {
      navigate('/admin');
    }
  }, [user, navigate]);

  return <Login onLogin={onLogin} />;
}

function AdminPanelWrapper({ user, onLogout }) {
  const navigate = useNavigate();
  useEffect(() => {
    if (!user) {
      navigate('/login');
    }
  }, [user, navigate]);

  return <AdminPanel onLogout={onLogout} />;
}
