import React, { useState, useEffect } from 'react';
import { Card, CardContent, Typography } from '@mui/material';
import UsersService from '../api/UsersService';
import { useTranslation } from 'react-i18next';
import './Users.css';


function Profile() {
    const [users, setUsers] = useState(null);
    const { t } = useTranslation();

    useEffect(() => {
        async function fetchUsers() {
            try {
                const users = await UsersService.getUsers();
                setUsers(users);
            } catch (error) {
                console.error('Failed to fetch users:', error);
            }
        }

        fetchUsers();
    }, []);

    if (!trainings) {
        return <Typography>{t("There is no users in system")}</Typography>;
    }

    return (
        <div className="trainings-container">
        {trainings.map((training) => (
            <Card key={training.uuid} className="training-card">
                <CardContent>
                    <Typography variant="h5" component="div">
                        {t('Training type')}: {training.type}
                    </Typography>
                    <Typography color="textSecondary">
                        {t('Start Time')}: {new Date(training.start_time).toLocaleString()}
                    </Typography>
                    <Typography color="textSecondary">
                        {t('End Time')}: {new Date(training.end_time).toLocaleString()}
                    </Typography>
                    <Typography variant="body2">
                        {t('Description')}: {training.description}
                    </Typography>
                </CardContent>
            </Card>
        ))}
    </div>
    );
}

export default Profile;
